package com.snaps.common.utils.net;import android.content.Context;import android.net.ConnectivityManager;import android.net.NetworkInfo;public class CNetStatus {	public static final int NET_TYPE_NONE = 0x00;	public static final int NET_TYPE_WIFI = 0x01;	public static final int NET_TYPE_3G = 0x02;	public static final int NET_TYPE_WIBRO = 0x03;	private static CNetStatus current = null;		public static CNetStatus getInstance() {		if(current == null) {			current = new CNetStatus();		}		return current;	}		private boolean getWibroStatus(Context p_oContext) {			ConnectivityManager cm = (ConnectivityManager) p_oContext.getSystemService(Context.CONNECTIVITY_SERVICE);		NetworkInfo ni = cm.getNetworkInfo(ConnectivityManager.TYPE_WIMAX);		if(ni == null) return false;		boolean isConn = ni.isConnected();		return isConn;			}		private boolean getWifiStatus(Context p_oContext) {		if (p_oContext == null) return false;		ConnectivityManager cm = (ConnectivityManager) p_oContext.getSystemService(Context.CONNECTIVITY_SERVICE);		NetworkInfo ni = cm.getNetworkInfo(ConnectivityManager.TYPE_WIFI);		if(ni == null) return false;		boolean isConn = ni.isConnected();		return isConn;	}		private boolean get3GStatus(Context p_oContext) {		ConnectivityManager cm = (ConnectivityManager) p_oContext.getSystemService(Context.CONNECTIVITY_SERVICE);		NetworkInfo ni = cm.getNetworkInfo(ConnectivityManager.TYPE_MOBILE);		if(ni == null) return false;		boolean isConn = ni.isConnected();		return isConn;			}		public int getNetType(Context p_oContext) {		int nNetType = CNetStatus.NET_TYPE_NONE;				if(getWifiStatus(p_oContext)) {			nNetType = CNetStatus.NET_TYPE_WIFI;		} else if(get3GStatus(p_oContext)) {			nNetType = CNetStatus.NET_TYPE_3G;		} else if(getWibroStatus(p_oContext)) {			nNetType = CNetStatus.NET_TYPE_WIBRO;		}		return nNetType;	}		public boolean isAliveNetwork(Context context) {		return getNetType(context) != CNetStatus.NET_TYPE_NONE;	}	public boolean isWifiConnected(Context context) {		return getNetType(context) != CNetStatus.NET_TYPE_NONE;	}	public static boolean isEnableAppLaunchNetworkState(Context context) {		CNetStatus net_status = CNetStatus.getInstance();		switch (net_status.getNetType(context)) {			case CNetStatus.NET_TYPE_WIFI:			case CNetStatus.NET_TYPE_3G:			case CNetStatus.NET_TYPE_WIBRO:				return true;			case CNetStatus.NET_TYPE_NONE:				return false;		}		return false;	}}